         private void byteWrite(long value) {
             long temp = value / 128L; // 將值除以128並存儲在臨時變量temp中
             if (temp > 0L) { // 如果temp大於0
                 writeC(hextable[(int)value % 128]); // 寫入值除以128的餘數對應的hextable值
                 while (temp >= 128L) { // 當temp大於或等於128時
                     writeC(hextable[(int)temp % 128]); // 寫入temp除以128的餘數對應的hextable值
                     temp /= 128L; // 將temp除以128
                 }
                 if (temp > 0L) { // 如果temp大於0
                     writeC((int)temp); // 寫入temp值
                 }
             } else if (value == 0L) { // 如果值等於0
                 writeC(0); // 寫入0
             } else { // 如果temp小於或等於0且值不等於0
                 writeC(hextable[(int)value]); // 寫入值對應的hextable值
                 writeC(0); // 寫入0
             }
         }

